//	This method is executed for every returned array from getAllForPin().
//	It then generates the url to delete each document from the cloudant database.

var newmsg = {};

if (msg.docId == null || msg.docRev == null)
	return null;
else {

	newmsg.url = "https://" + context.global.cloudantUser + ":" + context.global.cloudantPassword 
			+ "@" + context.global.cloudantDBURL + msg.docId + "?rev=" + msg.docRev; 
	newmsg.method = "DELETE";
	newmsg.headers = { 'IBM-Application-Secret' : context.global.appSecret};
	newmsg.payload = {};

	return newmsg;
}

//	This method takes in a device pin as an input (msg.device_pin)
//	and prepares the url for the GET request. It gets all the device
//	instance and sensor data documents associated with that device pin. 

var newmsg = {};
newmsg.device_pin = msg.device_pin;
newmsg.url = "https://" + context.global.cloudantUser + ":" + context.global.cloudantPassword 
			+ "@" + context.global.cloudantDBURL + "_design/library/_view/delete_view_by_pin?key=\"" 
			+ newmsg.device_pin  +"\"&limit=" + context.global.queryLimit; 
newmsg.method = "GET";
//clearing the payload from unnecessary data
newmsg.payload = {} ; 


return  newmsg;

//	This method parses results from the GET request.

var result = JSON.parse(msg.payload); 
var retData = []; 
//	For all the rows in the results, extract the document id and rev
for (i = 0; i < result.rows.length; i++){
	var jsonIdRev = {};
	jsonIdRev.docId = result.rows[i].value._id;		
	jsonIdRev.docRev = result.rows[i].value._rev;
	//add each pair to the return array
	retData.push(jsonIdRev);	
}

//	returning an array of arrays since each returned array 
//	is considered as a separate message going into the rest of the flow
return [retData];

//	This method prepares the body for the POST request
//	that will create the appropriate tag for IBM Push
//	Note: needs the app secret for authentication


var newmsg = {};


newmsg.headers = { 'IBM-Application-Secret' : context.global.appSecret };

newmsg.url = context.global.pushURL + "tags"; 
newmsg.payload = {
				  "name": msg.device_pin,
				  "description" : "IBM Push tag for IoT deviceId : " + msg.device_pin
			     };

return newmsg;

//	This method parses the data recived from the simulator app.
//	It determines whether to register or unregister a specific tag.


msg.payload = JSON.parse(msg.payload);


//  The [msg.payload, null] returned array format
//  is to choose the appropriate output port. 
if (msg.payload.register === 1)
	return [msg.payload, null]; 
else if(msg.payload.register === 0)
	return [null, msg.payload];

//	This method prepares the body for the DELETE request
//	that will delete the appropriate tag for IBM Push
//	Note: needs the app secret for authentication


var newmsg = {};

newmsg.url = context.global.pushURL + "tags/" + msg.device_pin; 
//newmsg.url = context.global.tagURL + msg.device_pin; 
newmsg.method = "DELETE";
newmsg.headers = { 'IBM-Application-Secret' : context.global.appSecret  };
newmsg.payload = {};

return newmsg;
